	          	}
	        } );

			return false;
		} );

		jQuery( \'a.toggle-poll-state\' ).click( function() {
			var data = {};
			var button = jQuery(this);
			var open = \'Open Poll\';
			var close = \'Close Poll\';

			data[\'_pd_nonce\'] = \'3c9acf6d09\';
			data[\'id\']        = 7012505;

