<script type="9482db9761c5f7450f2eb328-text/javascript">

        /**
         *
         * @param {string} dataType
         * @param {string} methodType
         * @param {string} url
         * @param {object} data
         * @param {boolean} async
         * @returns {jqXHR}
         */
        function sendAjaxRequest(dataType, methodType, url, data, async = true) {
            return $.ajax({
                dataType: dataType,
                type: methodType,
                url: url,
                data: data,
                statusCode: {
                    404: function () {
                        alert("page not found");
                    }
                },
                async: async
            });
        }

        function fav(id) {
            const startSelector = $("#star_" + id);
            if (startSelector.attr(\'src\').includes("/img/icon_fav_on.jpg?v3") > 0) {
                sendAjaxRequest(\'json\', \'POST\', \'/ajax/remove-favorite/varSnippetId\'.replace(\'varSnippetId\', id), {})
                    .done(data => {
                        startSelector.attr(\'src\', "/img/icon_fav_off.jpg?v3");
                    });
            } else {
                sendAjaxRequest(\'json\', \'POST\', \'/ajax/add-favorite/varSnippetId\'.replace(\'varSnippetId\', id), {})
                    .done(data => {
                        startSelector.attr(\'src\', "/img/icon_fav_on.jpg?v3");
                    });
            }
            return false;
        }

        let selectedSnippet = 0;

        function loadFolderList(snippetId) {
            selectedSnippet = snippetId;
            $(\'#folder-html\').html("");
            $(\'#folder-menu\').show(\'fast\');
            $(\'#indicator\').show();
            sendAjaxRequest(\'json\', \'GET\', \'/folder-snippet-list/varSnippet\'.replace(\'varSnippet\', snippetId), {})
                .done(item => {
                    $(\'#indicator\').hide();
                    let html = \'<ul class="folder-list">\';
                    Object.entries(item.list).forEach(([key, value]) => {
                        html += "<li><label><input type=\'checkbox\' class=\'folder-item\' value=\'" + key + "\' " + (value.checked ? \'checked\' : \'\') + "> " + value.name + "</label></li>";
                    });
                    html += \'</ul>\';
                    $(\'#folder-html\').html(html);

                    $(\'.folder-item:checkbox\').change(function () {
                        const act = ($(this).is(\':checked\')) ? "/ajax/add-folder-snippet/varSnippet/varFolder" : "/ajax/remove-folder-snippet/varSnippet/varFolder";
                        const url = act.replace(\'varSnippet\', snippetId).replace("varFolder", $(this).val());
                        sendAjaxRequest(\'json\', \'POST\', url, {})
                            .done(data => {
                                const folderMsgSelector = $(\'#folder-msg\');
                                if ($(this).is(\':checked\')) {
                                    folderMsgSelector.html(\'Snippet added to folder\');
                                } else {
                                    folderMsgSelector.html(\'Snippet removed from folder\');
                                }
                            });
                    });
                });
        }

        $(\'#folder_close\').click(function () {
            $(\'#folder-menu\').hide(\'fast\');
        });
        $(\'#folder-add\').submit(function () {
            const folderNameSelector = $(\'#folder-name\');
            let name = $.trim(folderNameSelector.val());
            $(\'#folder-error\').text("");
            sendAjaxRequest(\'json\', \'POST\', \'/add-folder\', {\'name\': name})
                .done(item => {
                    if(item.status === "SUCCESS") {
                        folderNameSelector.val("");
                        loadFolderList(selectedSnippet);
                    }else{
                        $(\'#folder-error\').text(item.errors.join(\', \'));
                    }

                });
            return false;
        });

    </script>
<script type="9482db9761c5f7450f2eb328-text/javascript">
                                $(function () {
            loadAllComments();
        });

        function submitComment() {
            const commentSelector = $(\'textarea#comment\');
            const comment = commentSelector.val();
            const ajaxResponse = sendAjaxRequest(\'json\', \'POST\', "/ajax/add-comment/72914", {\'comment\': comment});
            ajaxResponse.done(data => {
                if (data.status === "SUCCESS") {
                    commentSelector.val(\'\');
                    loadAllComments();
                } else {
                    $(\'#comment-error\').text(data.error);
                }
            });
        }

        function loadAllComments() {
            $(\'#commentList\').text("Loading..");

            const ajaxResponse = sendAjaxRequest(\'json\', \'GET\', "/ajax/comments/72914", {});
            ajaxResponse.done(data => {
                let commentContentList = [];
                data.list.forEach(item => {
                    let commentText = "";
                    let path = "/users/varUserName";
                    commentText += \'<dt><span class="smaller">Posted By: <a href="\' + path.replace(\'varUserName\', item.username) + \'">\' + item.username + \'</a> on \' + item.datePosted;
                                        commentText += \'</span></dt>\';

                                                                                commentText += \'<dd>\' + item.comment + \'</dd>\';
                    commentContentList.push(commentText);
                });
                $(\'#commentList\').html(commentContentList.join(\'\'));
            });
        }

        function likeDislikeComment(id) {
            const likeCountSelector = $(\'#like_cnt_\' + id);
            const likeIconSelector = $(\'#like_icon_\' + id);
            if (likeIconSelector.attr("data-flag") === \'1\') {
                const addLikePath = "/ajax/add-like/varCommentId";
                const ajaxResponse = sendAjaxRequest(\'json\', \'POST\', addLikePath.replace(\'varCommentId\', id), {});
                ajaxResponse.done(data => {
                    likeIconSelector.attr("class", \'comment-like-icon\');
                    likeIconSelector.attr(\'data-flag\', "0");
                    likeCountSelector.text(parseInt(likeCountSelector.text()) + 1);
                });
                return false;
            } else {
                const removeLikePath = "/ajax/remove-like/varCommentId";
                const ajaxResponse = sendAjaxRequest(\'json\', \'POST\', removeLikePath.replace(\'varCommentId\', id), {});
                ajaxResponse.done(data => {
                    likeIconSelector.attr("class", \'comment-dislike-icon\');
                    likeIconSelector.attr(\'data-flag\', "1");
                    likeCountSelector.text(parseInt(likeCountSelector.text()) - 1);
                });
                return false;
            }
        }

        function referral_feedback(sourceId, targetId, score) {
            const path = (score === 1) ? "/ajax/good-suggestion/varSourceId/varTargetId" : "/ajax/bad-suggesion/varSourceId/varTargetId";
            sendAjaxRequest(\'json\', \'POST\', path.replace(\'varSourceId\', sourceId).replace(\'varTargetId\', targetId), {})
                .done(data => {
                    $("#recommendation").html(data.message);
                });
            return false;
        }

        function spam(id) {
            if (confirm("Are you sure you want to mark this snippet as spam?  You cannot undo this action.")) {
                const ajaxResponse = sendAjaxRequest(\'json\', \'POST\', \'/ajax/spam-snippet/72914\', {});
                ajaxResponse.done(data => {
                    $("#spam").html("<em>Thank you for helping to keep Snipplr spam-free!  Your request has been noted and appropriate action will be taken.</em>");
                });
            }
            return false;
        }

        function expand() {
            const mainCol = $(\'#maincol\');
            const sideCol = $(\'#sidecol\');
            const toggleExpand = $(\'#toggleexpand\');
            if (mainCol.attr(\'class\') === "expand") {
                sideCol.show();
                mainCol.attr(\'class\', \'\');
                toggleExpand.text("Expand");
            } else {
                sideCol.hide();
                mainCol.attr(\'class\', "expand");
                toggleExpand.text("Collapse");
            }
        }

    </script>
