                return [\'webkit\', parseInt(matches[1]), parseInt(matches[2])];
            }
        }

        function getIEVersion(){
            var agent = navigator.userAgent;
            var reg = /MSIE\\s?(\\d+)(?:\\.(\\d+))?/i;
